    enforced: false,
    deferrable: false,
    initially: "IMMEDIATE",
    comment: "hello fk",
});
const unique = new snowflake.TableConstraint("unique", {
    type: "UNIQUE",
    tableId: table.id,
    columns: ["col3"],
    comment: "hello unique",
});
